        static bool is_token_conjunctive(int token) {
            return token == TOKEN_AND || token == TOKEN_OR;
        }

        static bool is_token_value(int token) {
            return token == TOKEN_STRING || token == TOKEN_INTEGER || token == TOKEN_FLOAT;
        }

